void setup()
{
  Serial.begin(baudRate);
  pinMode(led7, OUTPUT);
  pinMode(led6, OUTPUT);
  pinMode(led5, OUTPUT);
  pinMode(led4, OUTPUT);
  pinMode(led3, OUTPUT);
  pinMode(led2, OUTPUT);
  pinMode(led1, OUTPUT);
  pinMode(led0, OUTPUT);
}

void loop()
{ 
  for (int i = 0; i < 8; i++)
  {
    Serial.printf("%d\n", i);
    print_character('1', i);
    delay(1000);
  }
  
}
void printColumn(int column)
{
  digitalWrite(led7, characters[0][column]);
  digitalWrite(led6, characters[1][column]);
  digitalWrite(led5, characters[2][column]);
  digitalWrite(led4, characters[3][column]);
  digitalWrite(led3, characters[4][column]);
  digitalWrite(led2, characters[5][column]);
  digitalWrite(led1, characters[6][column]);
  digitalWrite(led0, characters[7][column]);
}
void printSerialColumn(int column){
  Serial.println(characters[0][column]);
  Serial.println(characters[1][column]);
  Serial.println(characters[2][column]);
  Serial.println(characters[3][column]);
  Serial.println(characters[4][column]);
  Serial.println(characters[5][column]);
  Serial.println(characters[6][column]);
  Serial.println(characters[7][column]);
}
void input_character(char c, int column){
  int id = (int) c;
  if (id >= 48 && id <= 122)
  {
    printColumn((column-48)*8);
  }
}
void print_character(char c, int column){
  int id = ((int) c);
  if (id >= 48 && id <= 122)
  {
    Serial.printf("c = %c id = %d", c, id);
    int column_id = (id - 48) * 8 + column;
    printColumn(column_id);
    Serial.printf(" column_id = %d\n", column_id);
  }
}